mapper.select:
select count(*) from
  project JOIN project_user
    ON project.project_id = project_user.project_id
    where project_user.user_id = #{user_id}
    and project.project_name =#{project_name}

select count(*) from
  project JOIN project_user
    ON project.project_id = project_user.project_id
    where project_user.user_id = #{user_id}
    and project.project_key =#{project_key}
